window.onload = function () {
    console.log("Loaded")
    loadSavedData();
};

function addBlock() {
    // Получаем значения из полей ввода
    let name = document.getElementById("inputFieldName").value;
    let imgUri = document.getElementById("inputFieldImg").value;
    let description = document.getElementById("inputFieldDescription").value;
    let id = document.getElementById("inputFieldId").value;
    let provider = document.getElementById("inputFieldProvider").value;

    createCard(name, imgUri, description, id, provider)

    saveData(name, imgUri, description, id, provider)
    // Очистка поля ввода
    document.getElementById("inputFieldName").value = "";
    document.getElementById("inputFieldImg").value = "";
    document.getElementById("inputFieldDescription").value = "";
    document.getElementById("inputFieldId").value = "";
    document.getElementById("inputFieldProvider").value = "";

    // Сброс валидации формы
    document.getElementById("inputForm").reset();
    // Заблокировать кнопку после добавления блока
    document.getElementById("addButton").disabled = true;
}

function createCard(name, imgUri, description, id, provider) {
    // Создаем новую карточку с введенными данными
    let resultBlock = document.createElement("div");
    resultBlock.className = "result-block";
    resultBlock.innerHTML = "<div class=\"card\">\n" +
        "            <div>\n" +
        "              <div class=\"card-other\">Id: " + id + "</div>\n" +
        "              <div class=\"card-other\">Поставщик: " + provider + "</div>\n" +
        "              <div class=\"photo-and-name\">\n" +
        "                  <img class=\"card-photo\" alt=\"card photo\" src=\"" + imgUri + "\">\n" +
        "                  <div class=\"card-name\">" + name + "</div>\n" +
        "                </div>\n" +
        "              <div class=\"card-description\">" + description + "</div>\n" +
        "            </div>\n" +
        "          </div>"

    // Добавляем блок в правую половину
    document.querySelector('.cards-container').appendChild(resultBlock);
}

